    keys = df.count().sort_values(ascending=False)[:k].index.tolist()

    category_names = ['Negative', 'Positive']
    scores = {}

    for key in keys:
        scores[key] = [(df[key] == category_names[0]).sum(), (df[key] == category_names[1]).sum()]

    return scores, category_names
